(1138, 1, 'Mariel', 56, 4, 0, '#include<iostream>\n#include<string>\n#include<cmath>\nusing namespace std;\nbool posible(int v[],int des){\n while(des>0){\n    int d=des%10;\n    if(!v[d])return false;\n    des/=10;\n }\n return true;\n}\nint respuesta(int v[],int des){\n    int up=1,down=1;\n    int arriba=des+1,abajo=des-1;\n    while(!posible(v,arriba)){\n        up++;\n        arriba++;\n        if(arriba>999){\n            up=1000;\n            break;\n        }\n    }\n    while(!posible(v,abajo)){\n        down++;\n        abajo--;\n        if(abajo<0){\n            down=1000;\n            break;\n        }\n    }\n    if(up<down)return up;\n    else return down;\n}\nint main(){\nint n,x,des;\ncin>>n;\nint c[10];\nfor(int i=0;i<10;i++){\n    c[i]=1;\n}\nfor(int i=0;i<n;i++){\n    cin>>x;\n    c[x]=0;\n}\ncin>>des;\nint up=0,down=0;\nif(posible(c,des)){\n    cout<<0;\n}\nelse{\n    cout<<respuesta(c,des);\n}\nreturn 0;\n}\n', 2084, '181.114.171.2', '2019-10-04 19:47:10', 0, 15),
